import React, { useEffect, useState } from 'react';

function ReporteGral() {
  const [ventasMensuales, setVentasMensuales] = useState(null);
  const [topProductos, setTopProductos] = useState([]);
  const [menosVendidos, setMenosVendidos] = useState([]);

  useEffect(() => {
    // Obtener ventas mensuales
    fetch('http://localhost:3001/api/ventas-mensuales')
      .then((response) => response.json())
      .then((data) => setVentasMensuales(data.totalVentas))
      .catch((error) => console.error('Error fetching ventas mensuales:', error));

    // Obtener top productos
    fetch('http://localhost:3001/api/top-productos')
      .then((response) => response.json())
      .then((data) => setTopProductos(data))
      .catch((error) => console.error('Error fetching top productos:', error));

    // Obtener productos menos vendidos
    fetch('http://localhost:3001/api/menos-vendidos')
      .then((response) => response.json())
      .then((data) => setMenosVendidos(data))
      .catch((error) => console.error('Error fetching menos vendidos:', error));
  }, []);

  return (
    <div>
      <h1>Reporte General</h1>
      <h2>Ventas Mensuales: {ventasMensuales !== null ? ventasMensuales : 'Cargando...'}</h2>

      <h3>Top Productos</h3>
      <ul>
        {topProductos.map((producto, index) => (
          <li key={index}>{producto}</li>
        ))}
      </ul>

      <h3>Productos Menos Vendidos</h3>
      <ul>
        {menosVendidos.map((producto, index) => (
          <li key={index}>{producto}</li>
        ))}
      </ul>
    </div>
  );
}

export default ReporteGral;
