import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [totalVentas, setTotalVentas] = useState(0);
  const [topProductos, setTopProductos] = useState([]);
  const [lessSoldProducts, setLessSoldProducts] = useState([]);

  useEffect(() => {
    fetchTotalVentas();
    fetchTopProductos();
    fetchLessSoldProducts();
  }, []);

  const fetchTotalVentas = async () => {
    try {
      const response = await axios.get('/api/ventas-mensuales');
      setTotalVentas(response.data.totalVentas);
    } catch (error) {
      console.error('Error fetching total ventas:', error);
    }
  };

  const fetchTopProductos = async () => {
    try {
      const response = await axios.get('/api/top-productos');
      setTopProductos(response.data);
    } catch (error) {
      console.error('Error fetching top productos:', error);
    }
  };

  const fetchLessSoldProducts = async () => {
    try {
      const response = await axios.get('/api/menos-vendidos');
      setLessSoldProducts(response.data);
    } catch (error) {
      console.error('Error fetching less sold products:', error);
    }
  };

  return (
    <div className="App">
      <Sidebar />
      <div className="main-content">
        <h2>Ventas del último mes: ${totalVentas.toLocaleString()} pesos</h2>

        <h3>Productos más vendidos del mes</h3>
        <ProductTable products={topProductos} />

        <h3>Productos menos vendidos del mes</h3>
        <ProductTable products={lessSoldProducts} />
      </div>
    </div>
  );
};

const Sidebar = () => (
  <div className="sidebar">
    <a href="/"><i className="fa fa-home"></i> Menu</a>
    {/* Aquí continúan las demás opciones de menú */}
  </div>
);

const ProductTable = ({ products }) => (
  <table className="product-table">
    <thead>
      <tr>
        <th>Producto</th>
        <th>Total Cantidad</th>
        <th>Total Ventas</th>
      </tr>
    </thead>
    <tbody>
      {products.map((product, index) => (
        <tr key={index}>
          <td>{product.NOMBRE_PRODUCTO}</td>
          <td>{product.TOTAL_CANTIDAD}</td>
          <td>${product.TOTAL_VENTAS.toLocaleString()}</td>
        </tr>
      ))}
    </tbody>
  </table>
);

export default App;
