import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [totalVentas, setTotalVentas] = useState(0);

  useEffect(() => {
    fetchTotalVentas();
  }, []);

  const fetchTotalVentas = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/ventas-mensuales');
      setTotalVentas(response.data.totalVentas);
    } catch (error) {
      console.error('Error fetching total ventas:', error);
    }
  };

  return (
    <div className="App">
      <Sidebar />
      <div className="main-content">
        <h2>Ventas del último mes: ${totalVentas} pesos</h2>
      </div>
    </div>
  );
};

const Sidebar = () => (
  <div className="sidebar">
    <a href="/"><i className="fa fa-home"></i> Menu</a>
    {/* Aquí continúan las demás opciones de menú si las necesitas */}
  </div>
);

export default App;
