import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [totalVentas, setTotalVentas] = useState(0);
  const [topProductos, setTopProductos] = useState([]);
  const [menosVendidos, setMenosVendidos] = useState([]);

  useEffect(() => {
    fetchTotalVentas();
    fetchTopProductos();
    fetchMenosVendidos();
  }, []);

  const fetchTotalVentas = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/ventas-mensuales');
      setTotalVentas(response.data.totalVentas);
    } catch (error) {
      console.error('Error fetching total ventas:', error);
    }
  };

  const fetchTopProductos = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/top-productos');
      setTopProductos(response.data); // Suponiendo que `data` es un array de productos
    } catch (error) {
      console.error('Error fetching top productos:', error);
    }
  };

  const fetchMenosVendidos = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/menos-vendidos');
      setMenosVendidos(response.data); // Suponiendo que `data` es un array de productos
    } catch (error) {
      console.error('Error fetching menos vendidos:', error);
    }
  };

  return (
    <div className="App">
      <Sidebar />
      <div className="main-content">
        <h2>Ventas del último mes: ${totalVentas.toLocaleString()} pesos</h2>
        <h3>Productos más vendidos:</h3>
        <ul>
          {topProductos.map((producto, index) => (
            <li key={index}>
              {producto[0]} - {producto[1]} vendidos - ${producto[2].toLocaleString()}
            </li>
          ))}
        </ul>
        <h3>Productos menos vendidos:</h3>
        <ul>
          {menosVendidos.map((producto, index) => (
            <li key={index}>
              {producto[0]} - {producto[1]} vendidos - ${producto[2].toLocaleString()}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

const Sidebar = () => (
  <div className="sidebar">
    <a href="/"><i className="fa fa-home"></i> Menu</a>
    {/* Aquí continúan las demás opciones de menú si las necesitas */}
  </div>
);

export default App;
